function wpjam_edit_user_profile_update($user_id){ 
	if (!current_user_can('edit_user', $user_id)) 
		return false; 
	$user = get_userdata($user_id); 
	$_POST['nickname'] = ($_POST['nickname'])?:$user->user_login;
	$_POST['display_name']	= $_POST['nickname'];
	$_POST['first_name']	= '';
	$_POST['last_name']		= '';
}

